import { useState, useEffect } from 'react';
import './FilePreview.css';

function FilePreview() {
  const [selectedFile, setSelectedFile] = useState(null);

  function showPreview(file) {
    let fileType = file.split('.').pop();
    if (fileType === "mp4") {
      setSelectedFile(
        <div id="previewOverlay">
          <video id="mediaPreview" width="50%" controls muted="muted" loop autoPlay>
            <source src={"/video/" + file} type="video/mp4" />
          </video>
        </div>
      );
    }
    if (fileType === "png") {
      setSelectedFile(
        <div id="previewOverlay">
          <img id="mediaPreview" src={"/photo/" + file} alt={file} width="50%" height="50%"/>
        </div>
      );
      // console.log("/photo/" + file);
    }
    if (fileType === "pdf") {
      setSelectedFile(
        <div>
          <embed src="https://sumanbogati.github.io/tiny.pdf" width="600px" height="500px" />
        </div>
        );
    }
    // console.log(file.split('.').pop());
  }

  const [data, setData] = useState(null);

  useEffect(() => {
    fetch("/files")
      .then((res) => res.json())
      .then((data) => {
        let divs = [];
        data.array.forEach(element => {
          divs.push(
          <div 
            key={element}
            className="file-preview__container"
            onClick={() => showPreview(element)}
          >
            <h1>{element}</h1>
          </div>
          );
        });
        setData(divs);
      })
  }, []);

  return (
      <div className="file-preview__body">
        {!data ? "Loading..." : data}
        {selectedFile}
      </div>
  );
}

export default FilePreview;
