import { useState } from 'react';
import './App.css';
import FilePreview from './FilePreview';

function App() {
  const [style, setStyle] = useState("fadeIn");

  const toggleFade = () => {
    if (style === "fadeOut") {
      setStyle("fadeIn")
    } else {
      setStyle("fadeOut");
    }
    console.log(style);
  }

  return (
    <div className="App">
      <FilePreview />
      <div className={style}>
        <div className="overlay" onClick={toggleFade}>
        </div>
      </div>
    </div>
  );
}

export default App;
