import { useState } from 'react';
import './App.css';
import FilePreview from './FilePreview';
import CSSTransitionGroup from 'react-transition-group';

function App() {
  const [style, setStyle] = useState(false);

  const toggleFade = () => {
    if (style === true) {
      setStyle(false);
    } else {
      setStyle(true);
    }
    console.log(style);
  }

  return (
    <div className="App">
      <FilePreview />
      {/* <div className={style}>
        <div className="overlay" onClick={toggleFade}>
        </div>
      </div> */}
      <button onClick={toggleFade()}>Add Item</button>
      <CSSTransitionGroup
        in={style}
        classNames="example"
        timeout={500}
        unmountOnExit>
        <div>test</div>
      </CSSTransitionGroup>
    </div>
  );
}

export default App;
