import { useState, useEffect } from 'react';
import './FileIcons.css';
// import FilePreview from './Filepreview';

function FileIcons() {
  // PREVIEW OVERLAY
  const [selectedFile, setSelectedFile] = useState(null);
  
  function showPreview(file) {
    let fileType = file.split('.').pop();
    var media;
    switch (fileType) {
      case "mp4":
        media = 
        <video id="mediaPreview" width="50%" controls muted="muted" loop autoPlay>
          <source src={"/media/" + file} type="video/mp4" />
        </video>
        break;
      case "png":
      case "jpg":
        media = <img id="mediaPreview" src={"/media/" + file} alt={file} width="70%" height="70%"/>
        break;
      default:
        media = <div>Test</div>
    }
    setSelectedFile(<div id="previewOverlay">{media}</div>)
  }

  // HIGHLIGHTING ICONS
  // TODO: Create this with useRef instead...
  function highlightIcons(element) {
      fetch("/files")
        .then((res) => res.json())
        .then((data) => {
          data.array.forEach(element => {
            document.getElementById(element).style.backgroundColor = "white"
            document.getElementById(element).style.color = "#7A7A7A"
          });
          document.getElementById(element).style.backgroundColor = "#E8F0FE"
          document.getElementById(element).style.color = "#1967DA"
        })
  }

  // DYNAMIC FILE ICONS
  const [data, setData] = useState(null);

  useEffect(() => {
    fetch("/files")
      .then((res) => res.json())
      .then((data) => {
        let divs = [];
        data.array.forEach(element => {
          divs.push(
          <div 
            key={element}
            id={element}
            className="file-icon__container"
            onClick={() => highlightIcons(element)}
            onDoubleClick={() => showPreview(element)}
          >
            <h1>{element}</h1>
          </div>
          );
        });
        setData(divs);
      })
  }, []);

  return (
    <div style={{display: "flex", "flex-direction": "column"}}>
      <div className="file-icon__body">
        {!data ? "Loading..." : data}
        {selectedFile}
      </div>
    </div>
  );
}

export default FileIcons;
