import { useState, useEffect, useRef } from 'react';
import './FileIcons.css';
// import FilePreview from './Filepreview';

function useOutsideAlerter(ref) {
  useEffect(() => {
    /**
     * Alert if clicked on outside of element
     */
    function handleClickOutside(event) {
      if (ref.current && !ref.current.contains(event.target)) {
        alert("You clicked outside of me!");
      }
    }
    // Bind the event listener
    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      // Unbind the event listener on clean up
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, [ref]);
}

function FileIcons() {
  // PREVIEW OVERLAY
  const [selectedFile, setSelectedFile] = useState(null);
  
  function showPreview(file) {
    let fileType = file.split('.').pop();
    var media;
    switch (fileType) {
      case "mp4":
        media = 
        <video id="mediaPreview" width="50%" controls muted="muted" loop autoPlay>
          <source src={"/media/" + file} type="video/mp4" />
        </video>
        break;
      case "png":
      case "jpg":
        media = <img id="mediaPreview" src={"/media/" + file} alt={file} width="70%" height="70%"/>
        break;
      default:
        media = <div>Test</div>
    }
    setSelectedFile(<div id="previewOverlay">{media}</div>)
  }

  // DYNAMIC FILE ICONS
  const [data, setData] = useState(null);
  const wrapperRef = useRef(null);
  useOutsideAlerter(wrapperRef);

  useEffect(() => {
    fetch("/files")
      .then((res) => res.json())
      .then((data) => {
        let divs = [];
        data.array.forEach(element => {
          divs.push(
          <div 
            key={element}
            className="file-icon__container"
            ref={wrapperRef}
            onClick={() => handleRegionClick(element)}
            onDoubleClick={() => showPreview(element)}
          >
            <h1>{element}</h1>
          </div>
          );
        });
        setData(divs);
      })
  }, []);

  return (
      <div className="file-icon__body">
        {!data ? "Loading..." : data}
        {selectedFile}
      </div>
  );
}

export default FileIcons;
