import { useState, useEffect } from 'react';
import './FileIcons.css';
// import FilePreview from './Filepreview';

function FileIcons() {
  const [selectedFile, setSelectedFile] = useState(null);
  
  function showPreview(file) {
    let fileType = file.split('.').pop();
    var media;
    switch (fileType) {
      case "mp4":
        media = 
        <video id="mediaPreview" width="50%" controls muted="muted" loop autoPlay>
          <source src={"/media/" + file} type="video/mp4" />
        </video>
        break;
      case "png":
      case "jpg":
        media = <img id="mediaPreview" src={"/media/" + file} alt={file} width="70%" height="70%"/>
        break;
      default:
        media = <div>Test</div>
    }
    setSelectedFile(<div id="previewOverlay">{media}</div>)
  }

  const [data, setData] = useState(null);

  function handleRegionClick(id) {
    console.log(id);
  }

  useEffect(() => {
    fetch("/files")
      .then((res) => res.json())
      .then((data) => {
        let divs = [];
        data.array.forEach(element => {
          divs.push(
          <div 
            key={element}
            className="file-icon__container"
            onClick={() => handleRegionClick(element)}
            onDoubleClick={() => showPreview(element)}
          >
            <h1>{element}</h1>
          </div>
          );
        });
        setData(divs);
      })
  }, []);

  return (
      <div className="file-icon__body">
        {!data ? "Loading..." : data}
        {selectedFile}
      </div>
  );
}

export default FileIcons;
